import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import io from "socket.io-client";
import ReactDOM from 'react-dom';

//Style użyte w programie
const Page = styled.div`
  display: flex;
  height: 100%;
  width: 100%;
  align-items: start;
  background-color: #46516e;
  flex-direction: row;
`;


const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 500px;
  max-height: 500px;
  overflow: auto;
  width: 400px;
  border: 5px solid lightgray;
  border-radius: 10px;
  padding-bottom: 10px;
  margin-top: 25px;
`;
const Container1 = styled.div`
  display: flex;
  flex-direction: column;
  height: 250px;
  max-height: 500px;
  overflow: auto;
  width: 150px;
  border: 5px solid lightgray;
  border-radius: 10px;
  padding-bottom: 10px;
  margin-top: 25px;
`;


const Form = styled.form`
  width: 400px;
`;


const Row = styled.div`
width: 100%;

margin-right: 5px;
text-align: center;
border-top-right-radius: 10%;
border-bottom-right-radius: 10%;

`;

const MyRow = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
  margin-top: 10px;
`;

const MyMessage = styled.div`
  width: 100%;
  background-color: pink;
  color: #46516e;
  padding: 10px;
  margin-right: 5px;
  text-align: center;
  border-top-right-radius: 10%;
  border-bottom-right-radius: 10%;
`;

const PartnerRow = styled(MyRow)`
  justify-content: flex-start;
`;

const PartnerMessage = styled.div`
  width: 100%;
  background-color: pink;
  color: black;
  border: 1px solid lightgray;
  padding: 10px;
  margin-left: 5px;
  text-align: center;
  border-top-left-radius: 10%;
  border-bottom-left-radius: 10%;
`;


//Program głowny po stronie klienta
const App = () => {
  const [messages, setMessages] = useState([]);
  const socketRef = useRef();

    useEffect(() => {
    socketRef.current = io.connect('/');

    //Generowanie nazwy użytkownika
    var name = "User" + Math.floor(Math.random()*(123123-1+1)+1);
    socketRef.current.emit("new user", name);

    socketRef.current.on('send message', data => {
        const messageObject = {
        body: data
      };
      receivedMessage(messageObject);

    });


    //Przesyłanie listy aktywnych użytkowników 
    socketRef.current.on('server message', message => {
      var element = <div>{message.map(user => {return <li>{user}</li>})}</div>;
      ReactDOM.render(element, document.getElementById('1'));

    });


    socketRef.current.on("message", (message) => {
      receivedMessage(message);
    })


  }, []);


  //Funkcja do przekazywania wiadomości do okna użytkownika
  function receivedMessage(message) {
    setMessages(oldMsgs => [...oldMsgs, message]);
  }



  return (
    <Page>
      <Container1>
      <MyRow></MyRow>
      <Row>
            <class> Aktywni użytkownicy: </class>
      </Row>
      <MyRow></MyRow>
      <Row>
            <div id="1"></div>
      </Row>
      </Container1>


      <Container>
        <Row>
        <class> Wyniki pomiarów prędkości wiatru: </class></Row>
        <MyRow></MyRow>
        {messages.map((message, index) => {
            return (
              <MyRow key={index}>
                {
                // if(message.body.slice(2) >50)
               // {
                //   <PartnerMessage>
                //   {message.body}
                // </PartnerMessage>
               // }
                }

              <MyMessage>
                  {message.body}
              </MyMessage>

              </MyRow>
            )
        })}
      </Container>

    </Page>
  );

};

export default App;
