import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import io from "socket.io-client";
import ChatComponent from './ChatComponent';
import ReactDOM from 'react-dom';
const Page = styled.div`
  display: flex;
  height: 100vh;
  width: 100%;
  align-items: center;
  background-color: #46516e;
  flex-direction: row;
`;


const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 500px;
  max-height: 500px;
  overflow: auto;
  width: 400px;
  border: 1px solid lightgray;
  border-radius: 10px;
  padding-bottom: 10px;
  margin-top: 25px;
`;
const Container1 = styled.div`
  display: flex;
  flex-direction: column;
  height: 500px;
  max-height: 500px;
  overflow: auto;
  width: 200px;
  border: 1px solid lightgray;
  border-radius: 10px;
  padding-bottom: 10px;
  margin-top: 25px;
`;

const TextArea = styled.textarea`
  width: 98%;
  height: 100px;
  border-radius: 10px;
  margin-top: 10px;
  padding-left: 10px;
  padding-top: 10px;
  font-size: 17px;
  background-color: transparent;
  border: 1px solid lightgray;
  outline: none;
  color: lightgray;
  letter-spacing: 1px;
  line-height: 20px;
  ::placeholder {
    color: lightgray;
  }
`;

const Button = styled.button`
  background-color: pink;
  width: 100%;
  border: none;
  height: 50px;
  border-radius: 10px;
  color: #46516e;
  font-size: 17px;
`;

const Form = styled.form`
  width: 400px;
`;

const MyRow = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
  margin-top: 10px;
`;

const MyMessage = styled.div`
  width: 45%;
  background-color: pink;
  color: #46516e;
  padding: 10px;
  margin-right: 5px;
  text-align: center;
  border-top-right-radius: 10%;
  border-bottom-right-radius: 10%;
`;

const PartnerRow = styled(MyRow)`
  justify-content: flex-start;
`;

const PartnerMessage = styled.div`
  width: 100%;
  background-color: pink;
  color: black;
  border: 1px solid lightgray;
  padding: 10px;
  margin-left: 5px;
  text-align: center;
  border-top-left-radius: 10%;
  border-bottom-left-radius: 10%;
`;
var usersMap = new Map();
const App = () => {

  const [yourID, setYourID] = useState();
  const [users, setUsers] = useState([]);
  const [text,setText] = useState([]);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const socketRef = useRef();



  useEffect(() => {
    socketRef.current = io.connect('/');
 var name="user"+Math.floor(Math.random() * (123123 - 1+1)+1)+",  ";
    socketRef.current.emit("new user", name);

    socketRef.current.on('send message', data => {
      const messageObject = {
        body: data,
        id: yourID,
      };
      receivedMessage(messageObject);

    });

    socketRef.current.on('server message', message => {
      usersMap=message;

      var element = <div>{message.map(user => {return <li>{user}</li>})}</div>;
      ReactDOM.render(element, document.getElementById('1'));
    });




    socketRef.current.on("connect", () => {
      console.log(socketRef.current.id);
    });

    socketRef.current.on("message", (message) => {
      console.log("here");
      receivedMessage(message);
    })


  }, []);



  function receivedMessage(message) {
    setMessages(oldMsgs => [...oldMsgs, message]);
  }

  function receivedUsers(message) {
    setUsers(oldUsers => [message]);
  }

  function sendMessage(e) {
    e.preventDefault();
    const messageObject = {
      body: message,
      id: yourID,
    };
    setMessage("");
    socketRef.current.emit("send message", messageObject);
  }

  function handleChange(e) {
    setMessage(e.target.value);
  }


  return (
    <Page>
      <Container>

        <class> userlist </class>
        <MyRow></MyRow>
        {messages.map((message, index) => {
          if (message.id === yourID) {
            return (
              <MyRow key={index}>
                <MyMessage>
                  {message.body}
                </MyMessage>
              </MyRow>
            )
          }
          return (
            <PartnerRow key={index}>
              <PartnerMessage>
                {message.body}
              </PartnerMessage>
            </PartnerRow>
          )
        })}
      </Container>
      <Container1>
      <MyRow></MyRow>
      <div id="1"></div>
      </Container1>
      </Page>
  );

};

export default App;
